mapper.select:
		select id,year,name,description,status,start_time,end_time,create_time,create_user,update_time,update_user,udef1,udef2,udef3,udef4,udef5,udef6,udef7,udef8,udef9,udef10 from activity_info
		where 1=1
            AND id = #{obj.search.id}
            AND year = #{obj.search.year}
            AND name = #{obj.search.name}
            AND description = #{obj.search.description}
            AND status = #{obj.search.status}
            AND start_time = #{obj.search.startTime}
            AND end_time = #{obj.search.endTime}
            AND create_time = #{obj.search.createTime}
            AND create_user = #{obj.search.createUser}
            AND update_time = #{obj.search.updateTime}
            AND update_user = #{obj.search.updateUser}
            AND udef1 = #{obj.search.udef1}
            AND udef2 = #{obj.search.udef2}
            AND udef3 = #{obj.search.udef3}
            AND udef4 = #{obj.search.udef4}
            AND udef5 = #{obj.search.udef5}
            AND udef6 = #{obj.search.udef6}
            AND udef7 = #{obj.search.udef7}
            AND udef8 = #{obj.search.udef8}
            AND udef9 = #{obj.search.udef9}
            AND udef10 = #{obj.search.udef10}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,year,name,description,status,start_time,end_time,create_time,create_user,update_time,update_user,udef1,udef2,udef3,udef4,udef5,udef6,udef7,udef8,udef9,udef10 from activity_info
