mapper.update:
update activity_info
id=#{obj.id}, year=#{obj.year}, name=#{obj.name}, description=#{obj.description}, status=#{obj.status}, start_time=#{obj.startTime}, end_time=#{obj.endTime}, create_time=#{obj.createTime}, create_user=#{obj.createUser}, update_time=#{obj.updateTime}, update_user=#{obj.updateUser}, udef1=#{obj.udef1}, udef2=#{obj.udef2}, udef3=#{obj.udef3}, udef4=#{obj.udef4}, udef5=#{obj.udef5}, udef6=#{obj.udef6}, udef7=#{obj.udef7}, udef8=#{obj.udef8}, udef9=#{obj.udef9}, udef10=#{obj.udef10},
where id=#{obj.id} AND platform_id = #{platformId}

mapper.select:
		select * from activity_info
		where 1=1
            AND id = #{search.id}
            AND year like CONCAT('%',#{search.year},'%')
            AND name like CONCAT('%',#{search.name},'%')
            AND description = #{search.description}
            AND status = #{search.status}
            AND start_time = #{search.startTime}
            AND end_time = #{search.endTime}
            AND create_time = #{search.createTime}
            AND create_user = #{search.createUser}
            AND update_time = #{search.updateTime}
            AND update_user = #{search.updateUser}
            AND udef1 = #{search.udef1}
            AND udef2 = #{search.udef2}
            AND udef3 = #{search.udef3}
            AND udef4 = #{search.udef4}
            AND udef5 = #{search.udef5}
            AND udef6 = #{search.udef6}
            AND udef7 = #{search.udef7}
            AND udef8 = #{search.udef8}
            AND udef9 = #{search.udef9}
            AND udef10 = #{search.udef10}

         order by ${orderField} ${orderType}
