mapper.select:
		select id,activity_id,item_type,name,score,sort,vdef1,vdef2,vdef3 from activity_marking_setup
		where 1=1
            AND id = #{obj.search.id}
            AND activity_id = #{obj.search.activityId}
            AND item_type = #{obj.search.itemType}
            AND name = #{obj.search.name}
            AND score = #{obj.search.score}
            AND sort = #{obj.search.sort}
            AND vdef1 = #{obj.search.vdef1}
            AND vdef2 = #{obj.search.vdef2}
            AND vdef3 = #{obj.search.vdef3}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,activity_id,item_type,name,score,sort,vdef1,vdef2,vdef3 from activity_marking_setup
		where 1=1
            AND id = #{obj.id}
            AND activity_id = #{obj.activityId}
            AND item_type = #{obj.itemType}
            AND name = #{obj.name}
            AND score = #{obj.score}
            AND sort = #{obj.sort}
            AND vdef1 = #{obj.vdef1}
            AND vdef2 = #{obj.vdef2}
            AND vdef3 = #{obj.vdef3}

         AND platform_id = #{platformId}

mapper.update:
update activity_marking_setup
id=#{obj.id}, activity_id=#{obj.activityId}, item_type=#{obj.itemType}, name=#{obj.name}, score=#{obj.score}, sort=#{obj.sort}, vdef1=#{obj.vdef1}, vdef2=#{obj.vdef2}, vdef3=#{obj.vdef3},
where id=#{obj.id} AND platform_id = #{platformId}

mapper.select:
		select id,activity_id,item_type,name,score,sort,vdef1,vdef2,vdef3 from activity_marking_setup
		where 1=1
            AND id = #{obj.id}
            AND activity_id = #{obj.activityId}
            AND item_type = #{obj.itemType}
            AND name = #{obj.name}
            AND score = #{obj.score}
            AND sort = #{obj.sort}
            AND vdef1 = #{obj.vdef1}
            AND vdef2 = #{obj.vdef2}
            AND vdef3 = #{obj.vdef3}
        order by sort
