mapper.select:
		select id,activity_id,name,type,status,create_user,create_time,update_user,update_time,publish_time,user_apply_endtime,first_review_starttime,first_review_endtime,second_review_endtime from competition_item
		where 1=1
            AND id = #{obj.search.id}
            AND activity_id = #{obj.search.activityId}
            AND name = #{obj.search.name}
            AND type = #{obj.search.type}
            AND status = #{obj.search.status}
            AND create_user = #{obj.search.createUser}
            AND create_time = #{obj.search.createTime}
            AND update_user = #{obj.search.updateUser}
            AND update_time = #{obj.search.updateTime}
            AND publish_time = #{obj.search.publishTime}
            AND user_apply_endtime = #{obj.search.userApplyEndtime}
            AND first_review_starttime = #{obj.search.firstReviewStarttime}
            AND first_review_endtime = #{obj.search.firstReviewEndtime}
            AND second_review_endtime = #{obj.search.secondReviewEndtime}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,activity_id,name,type,status,create_user,create_time,update_user,update_time,publish_time,user_apply_endtime,first_review_starttime,first_review_endtime,second_review_endtime from competition_item
		where 1=1
            AND id = #{obj.id}
            AND activity_id = #{obj.activityId}
            AND name = #{obj.name}
            AND type = #{obj.type}
            AND status = #{obj.status}
            AND create_user = #{obj.createUser}
            AND create_time = #{obj.createTime}
            AND update_user = #{obj.updateUser}
            AND update_time = #{obj.updateTime}
            AND publish_time = #{obj.publishTime}
            AND user_apply_endtime = #{obj.userApplyEndtime}
            AND first_review_starttime = #{obj.firstReviewStarttime}
            AND first_review_endtime = #{obj.firstReviewEndtime}
            AND second_review_endtime = #{obj.secondReviewEndtime}

         AND platform_id = #{platformId}

mapper.update:
update competition_item
id=#{obj.id}, activity_id=#{obj.activityId}, name=#{obj.name}, type=#{obj.type}, status=#{obj.status}, create_user=#{obj.createUser}, create_time=#{obj.createTime}, update_user=#{obj.updateUser}, update_time=#{obj.updateTime}, publish_time=#{obj.publishTime}, user_apply_endtime=#{obj.userApplyEndtime}, first_review_starttime=#{obj.firstReviewStarttime}, first_review_endtime=#{obj.firstReviewEndtime}, second_review_endtime=#{obj.secondReviewEndtime},
where id=#{obj.id} AND platform_id = #{platformId}
