mapper.select:
		select id,name,parent_id,description,sort,path,if_leaf,platform_id,code,create_user from content_category
		where 1=1
            AND name = #{obj.search.name}
            AND parent_id = #{obj.search.parentId}
            AND description = #{obj.search.description}
            AND sort = #{obj.search.sort}
            AND path = #{obj.search.path}
            AND if_leaf = #{obj.search.ifLeaf}
            AND platform_id = #{obj.search.platformId}
            AND code = #{obj.search.code}
            AND create_user = #{obj.search.createUser}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,name,parent_id,description,sort,path,if_leaf,platform_id,code,create_user from content_category
		where 1=1
            AND name = #{obj.name}
            AND parent_id = #{obj.parentId}
            AND description = #{obj.description}
            AND sort = #{obj.sort}
            AND path = #{obj.path}
            AND if_leaf = #{obj.ifLeaf}
            AND platform_id = #{obj.platformId}
            AND code = #{obj.code}
            AND create_user = #{obj.createUser}

         AND platform_id = #{platformId}

mapper.update:
update content_category
name=#{obj.name}, parent_id=#{obj.parentId}, description=#{obj.description}, sort=#{obj.sort}, path=#{obj.path}, if_leaf=#{obj.ifLeaf}, platform_id=#{obj.platformId}, code=#{obj.code}, create_user=#{obj.createUser},
where id=#{obj.id} AND platform_id = #{platformId}

