		select id,name,parent_id,description,sort,path,if_leaf,platform_id,code from content_category
		where 1=1
         AND name like CONCAT('%',#{obj.name},'%') 
           AND parent_id = #{obj.parentId}
           AND description = #{obj.description}
            AND sort = #{obj.sort}
          AND path like CONCAT('%',#{obj.path},'%') 
            AND if_leaf = #{obj.ifLeaf}
            AND code = #{obj.code}
           AND platform_id=#{platformId}
           order by sort

