mapper.select:
		select id,parent_id,name,code,value,sort,description from dictionary
		where 1=1
            AND id = #{obj.search.id}
            AND parent_id = #{obj.search.parentId}
            AND name = #{obj.search.name}
            AND code = #{obj.search.code}
            AND value = #{obj.search.value}
            AND sort = #{obj.search.sort}
            AND description = #{obj.search.description}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,parent_id,name,code,value,sort,description from dictionary
		where 1=1
            AND id = #{obj.id}
            AND parent_id = #{obj.parentId}
            AND name = #{obj.name}
            AND code = #{obj.code}
            AND value = #{obj.value}
            AND sort = #{obj.sort}
            AND description = #{obj.description}

         AND platform_id = #{platformId}

mapper.update:
update dictionary
id=#{obj.id}, parent_id=#{obj.parentId}, name=#{obj.name}, code=#{obj.code}, value=#{obj.value}, sort=#{obj.sort}, description=#{obj.description},
where id=#{obj.id} AND platform_id = #{platformId}
