mapper.select:
		select id,org_id,item_id,limit_num,create_time,create_user,update_time,update_user,spare1,spare2,spare3 from item_org_limit
		where 1=1
            AND id = #{obj.search.id}
            AND org_id = #{obj.search.orgId}
            AND item_id = #{obj.search.itemId}
            AND limit_num = #{obj.search.limitNum}
            AND create_time = #{obj.search.createTime}
            AND create_user = #{obj.search.createUser}
            AND update_time = #{obj.search.updateTime}
            AND update_user = #{obj.search.updateUser}
            AND spare1 = #{obj.search.spare1}
            AND spare2 = #{obj.search.spare2}
            AND spare3 = #{obj.search.spare3}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,org_id,item_id,limit_num,create_time,create_user,update_time,update_user,spare1,spare2,spare3 from item_org_limit
		where 1=1
            AND id = #{obj.id}
            AND org_id = #{obj.orgId}
            AND item_id = #{obj.itemId}
            AND limit_num = #{obj.limitNum}
            AND create_time = #{obj.createTime}
            AND create_user = #{obj.createUser}
            AND update_time = #{obj.updateTime}
            AND update_user = #{obj.updateUser}
            AND spare1 = #{obj.spare1}
            AND spare2 = #{obj.spare2}
            AND spare3 = #{obj.spare3}

         AND platform_id = #{platformId}

mapper.update:
update item_org_limit
id=#{obj.id}, org_id=#{obj.orgId}, item_id=#{obj.itemId}, limit_num=#{obj.limitNum}, create_time=#{obj.createTime}, create_user=#{obj.createUser}, update_time=#{obj.updateTime}, update_user=#{obj.updateUser}, spare1=#{obj.spare1}, spare2=#{obj.spare2}, spare3=#{obj.spare3},
where id=#{obj.id} AND platform_id = #{platformId}
