mapper.select:
		select id,name,url,sort,level,parent_id,vdef1,vdef2,vdef3 from menu
		where 1=1
            AND id = #{obj.search.id}
            AND name = #{obj.search.name}
            AND url = #{obj.search.url}
            AND sort = #{obj.search.sort}
            AND level = #{obj.search.level}
            AND parent_id = #{obj.search.parentId}
            AND vdef1 = #{obj.search.vdef1}
            AND vdef2 = #{obj.search.vdef2}
            AND vdef3 = #{obj.search.vdef3}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,name,url,sort,level,parent_id,vdef1,vdef2,vdef3 from menu
		where 1=1
            AND id = #{obj.id}
            AND name = #{obj.name}
            AND url = #{obj.url}
            AND sort = #{obj.sort}
            AND level = #{obj.level}
            AND parent_id = #{obj.parentId}
            AND vdef1 = #{obj.vdef1}
            AND vdef2 = #{obj.vdef2}
            AND vdef3 = #{obj.vdef3}

         AND platform_id = #{platformId}

mapper.update:
update menu
id=#{obj.id}, name=#{obj.name}, url=#{obj.url}, sort=#{obj.sort}, level=#{obj.level}, parent_id=#{obj.parentId}, vdef1=#{obj.vdef1}, vdef2=#{obj.vdef2}, vdef3=#{obj.vdef3},
where id=#{obj.id} AND platform_id = #{platformId}

mapper.select:
		select * from menu
		where 1=1
            AND id = #{obj.id}
            AND name = #{obj.name}
            AND url = #{obj.url}
            AND sort = #{obj.sort}
            AND level = #{obj.level}
            AND parent_id = #{obj.parentId}
            AND vdef1 = #{obj.vdef1}
            AND vdef2 = #{obj.vdef2}
            AND vdef3 = #{obj.vdef3}
		order by sort
