mapper.select:
		select id,name,url from menu
		where 1=1
            AND id = #{obj.search.id}
            AND name = #{obj.search.name}
            AND url = #{obj.search.url}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,name,url from menu
		where 1=1
            AND id = #{obj.id}
            AND name = #{obj.name}
            AND url = #{obj.url}

         AND platform_id = #{platformId}

mapper.update:
    	update menu 
        
           id=#{obj.id},
           name=#{obj.name},
           url=#{obj.url},  
        
		where id=#{obj.id} 
         AND platform_id = #{platformId}
