mapper.select:
		select id,name from role
		where 1=1
            AND id = #{obj.search.id}
            AND name = #{obj.search.name}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,name from role
		where 1=1
            AND id = #{obj.id}
            AND name = #{obj.name}

         AND platform_id = #{platformId}

mapper.update:
    	update role 
        
           id=#{obj.id},
           name=#{obj.name},  
        
		where id=#{obj.id} 
         AND platform_id = #{platformId}
