mapper.select:
		select id,activity_id,item_id,item_type,apply_id,get_score,marking_user,marking_time,vdef1,vdef2,vdef3 from user_apply_marking_result
		where 1=1
            AND id = #{obj.search.id}
            AND activity_id = #{obj.search.activityId}
            AND item_id = #{obj.search.itemId}
            AND item_type = #{obj.search.itemType}
            AND apply_id = #{obj.search.applyId}
            AND get_score = #{obj.search.getScore}
            AND marking_user = #{obj.search.markingUser}
            AND marking_time = #{obj.search.markingTime}
            AND vdef1 = #{obj.search.vdef1}
            AND vdef2 = #{obj.search.vdef2}
            AND vdef3 = #{obj.search.vdef3}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,activity_id,item_id,item_type,apply_id,get_score,marking_user,marking_time,vdef1,vdef2,vdef3 from user_apply_marking_result
		where 1=1
            AND id = #{obj.id}
            AND activity_id = #{obj.activityId}
            AND item_id = #{obj.itemId}
            AND item_type = #{obj.itemType}
            AND apply_id = #{obj.applyId}
            AND get_score = #{obj.getScore}
            AND marking_user = #{obj.markingUser}
            AND marking_time = #{obj.markingTime}
            AND vdef1 = #{obj.vdef1}
            AND vdef2 = #{obj.vdef2}
            AND vdef3 = #{obj.vdef3}

         AND platform_id = #{platformId}

mapper.update:
update user_apply_marking_result
id=#{obj.id}, activity_id=#{obj.activityId}, item_id=#{obj.itemId}, item_type=#{obj.itemType}, apply_id=#{obj.applyId}, get_score=#{obj.getScore}, marking_user=#{obj.markingUser}, marking_time=#{obj.markingTime}, vdef1=#{obj.vdef1}, vdef2=#{obj.vdef2}, vdef3=#{obj.vdef3},
where id=#{obj.id} AND platform_id = #{platformId}

mapper.select:
		select id,activity_id,item_id,item_type,apply_id,get_score,marking_user,marking_time,vdef1,vdef2,vdef3 from user_apply_marking_result
		where 1=1
            AND id = #{obj.id}
            AND activity_id = #{obj.activityId}
            AND item_id = #{obj.itemId}
            AND item_type = #{obj.itemType}
            AND apply_id = #{obj.applyId}
            AND get_score = #{obj.getScore}
            AND marking_user = #{obj.markingUser}
            AND marking_time = #{obj.markingTime}
            AND vdef1 = #{obj.vdef1}
            AND vdef2 = #{obj.vdef2}
            AND vdef3 = #{obj.vdef3}
        	order by marking_user,vdef2
