mapper.select:
		select id,apply_id,user_id,open_id,ip,vote_time,activity_id,item_id,vdef1,vdef2,vdef3 from user_apply_vote
		where 1=1
            AND id = #{obj.search.id}
            AND apply_id = #{obj.search.applyId}
            AND user_id = #{obj.search.userId}
            AND open_id = #{obj.search.openId}
            AND ip = #{obj.search.ip}
            AND vote_time = #{obj.search.voteTime}
            AND activity_id = #{obj.search.activityId}
            AND item_id = #{obj.search.itemId}
            AND vdef1 = #{obj.search.vdef1}
            AND vdef2 = #{obj.search.vdef2}
            AND vdef3 = #{obj.search.vdef3}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,apply_id,user_id,open_id,ip,vote_time,activity_id,item_id,vdef1,vdef2,vdef3 from user_apply_vote
		where 1=1
            AND id = #{obj.id}
            AND apply_id = #{obj.applyId}
            AND user_id = #{obj.userId}
            AND open_id = #{obj.openId}
            AND ip = #{obj.ip}
            AND vote_time = #{obj.voteTime}
            AND activity_id = #{obj.activityId}
            AND item_id = #{obj.itemId}
            AND vdef1 = #{obj.vdef1}
            AND vdef2 = #{obj.vdef2}
            AND vdef3 = #{obj.vdef3}

         AND platform_id = #{platformId}

mapper.update:
update user_apply_vote
id=#{obj.id}, apply_id=#{obj.applyId}, user_id=#{obj.userId}, open_id=#{obj.openId}, ip=#{obj.ip}, vote_time=#{obj.voteTime}, activity_id=#{obj.activityId}, item_id=#{obj.itemId}, vdef1=#{obj.vdef1}, vdef2=#{obj.vdef2}, vdef3=#{obj.vdef3},
where id=#{obj.id} AND platform_id = #{platformId}
