mapper.select:
		select id,user_name,mail,password,create_time,real_name,status,sex from user_info
		where 1=1
            AND id = #{obj.search.id}
            AND user_name = #{obj.search.userName}
            AND mail = #{obj.search.mail}
            AND password = #{obj.search.password}
            AND create_time = #{obj.search.createTime}
            AND real_name = #{obj.search.realName}
            AND status = #{obj.search.status}
            AND sex = #{obj.search.sex}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,user_name,mail,password,create_time,real_name,status,sex from user_info
		where 1=1
            AND id = #{obj.id}
            AND user_name = #{obj.userName}
            AND mail = #{obj.mail}
            AND password = #{obj.password}
            AND create_time = #{obj.createTime}
            AND real_name = #{obj.realName}
            AND status = #{obj.status}
            AND sex = #{obj.sex}

         AND platform_id = #{platformId}

mapper.update:
update user_info
id=#{obj.id}, user_name=#{obj.userName}, mail=#{obj.mail}, password=#{obj.password}, create_time=#{obj.createTime}, real_name=#{obj.realName}, status=#{obj.status}, sex=#{obj.sex},
where id=#{obj.id} AND platform_id = #{platformId}
