mapper.select:
		select id,user_id,login_time,refer_site,login_type,spare1,spare2,spare3 from user_login_log
		where 1=1
            AND id = #{obj.search.id}
            AND user_id = #{obj.search.userId}
            AND login_time = #{obj.search.loginTime}
            AND refer_site = #{obj.search.referSite}
            AND login_type = #{obj.search.loginType}
            AND spare1 = #{obj.search.spare1}
            AND spare2 = #{obj.search.spare2}
            AND spare3 = #{obj.search.spare3}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,user_id,login_time,refer_site,login_type,spare1,spare2,spare3 from user_login_log
		where 1=1
            AND id = #{obj.id}
            AND user_id = #{obj.userId}
            AND login_time = #{obj.loginTime}
            AND refer_site = #{obj.referSite}
            AND login_type = #{obj.loginType}
            AND spare1 = #{obj.spare1}
            AND spare2 = #{obj.spare2}
            AND spare3 = #{obj.spare3}

         AND platform_id = #{platformId}

mapper.update:
update user_login_log
id=#{obj.id}, user_id=#{obj.userId}, login_time=#{obj.loginTime}, refer_site=#{obj.referSite}, login_type=#{obj.loginType}, spare1=#{obj.spare1}, spare2=#{obj.spare2}, spare3=#{obj.spare3},
where id=#{obj.id} AND platform_id = #{platformId}
