mapper.select:
		select user_id,role_id from user_role
		where 1=1
            AND user_id = #{obj.search.userId}
            AND role_id = #{obj.search.roleId}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select user_id,role_id from user_role
		where 1=1
            AND user_id = #{obj.userId}
            AND role_id = #{obj.roleId}

         AND platform_id = #{platformId}

mapper.update:
    	update user_role 
        
           user_id=#{obj.userId},
           role_id=#{obj.roleId},  
        
		where user_id=#{obj.userId}  and role_id=#{obj.roleId} 
         AND platform_id = #{platformId}
