mapper.select:
		select id,user_id,visit_time,refer_site,visit_type,spare1,spare2,spare3 from user_visit_log
		where 1=1
            AND id = #{obj.search.id}
            AND user_id = #{obj.search.userId}
            AND visit_time = #{obj.search.visitTime}
            AND refer_site = #{obj.search.referSite}
            AND visit_type = #{obj.search.visitType}
            AND spare1 = #{obj.search.spare1}
            AND spare2 = #{obj.search.spare2}
            AND spare3 = #{obj.search.spare3}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,user_id,visit_time,refer_site,visit_type,spare1,spare2,spare3 from user_visit_log
		where 1=1
            AND id = #{obj.id}
            AND user_id = #{obj.userId}
            AND visit_time = #{obj.visitTime}
            AND refer_site = #{obj.referSite}
            AND visit_type = #{obj.visitType}
            AND spare1 = #{obj.spare1}
            AND spare2 = #{obj.spare2}
            AND spare3 = #{obj.spare3}

         AND platform_id = #{platformId}

mapper.update:
update user_visit_log
id=#{obj.id}, user_id=#{obj.userId}, visit_time=#{obj.visitTime}, refer_site=#{obj.referSite}, visit_type=#{obj.visitType}, spare1=#{obj.spare1}, spare2=#{obj.spare2}, spare3=#{obj.spare3},
where id=#{obj.id} AND platform_id = #{platformId}
