mapper.select:
		select manager_id,org_id from manager_org
		where 1=1
            AND manager_id = #{obj.search.managerId}
            AND org_id = #{obj.search.orgId}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select manager_id,org_id from manager_org
		where 1=1
            AND manager_id = #{obj.managerId}
            AND org_id = #{obj.orgId}

         AND platform_id = #{platformId}

mapper.update:
    	update manager_org 
        
           manager_id=#{obj.managerId},
           org_id=#{obj.orgId},  
        
		where manager_id=#{obj.managerId}  and org_id=#{obj.orgId} 
         AND platform_id = #{platformId}
