mapper.select:
		select id,activity_id,item_id,apply_id,take_user_id,take_time,take_type,take_score,def1,def2,def3,def4 from product_take_record
		where 1=1
            AND id = #{obj.search.id}
            AND activity_id = #{obj.search.activityId}
            AND item_id = #{obj.search.itemId}
            AND apply_id = #{obj.search.applyId}
            AND take_user_id = #{obj.search.takeUserId}
            AND take_time = #{obj.search.takeTime}
            AND take_type = #{obj.search.takeType}
            AND take_score = #{obj.search.takeScore}
            AND def1 = #{obj.search.def1}
            AND def2 = #{obj.search.def2}
            AND def3 = #{obj.search.def3}
            AND def4 = #{obj.search.def4}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,activity_id,item_id,apply_id,take_user_id,take_time,take_type,take_score,def1,def2,def3,def4 from product_take_record
		where 1=1
            AND id = #{obj.id}
            AND activity_id = #{obj.activityId}
            AND item_id = #{obj.itemId}
            AND apply_id = #{obj.applyId}
            AND take_user_id = #{obj.takeUserId}
            AND take_time = #{obj.takeTime}
            AND take_type = #{obj.takeType}
            AND take_score = #{obj.takeScore}
            AND def1 = #{obj.def1}
            AND def2 = #{obj.def2}
            AND def3 = #{obj.def3}
            AND def4 = #{obj.def4}

         AND platform_id = #{platformId}

mapper.update:
update product_take_record
id=#{obj.id}, activity_id=#{obj.activityId}, item_id=#{obj.itemId}, apply_id=#{obj.applyId}, take_user_id=#{obj.takeUserId}, take_time=#{obj.takeTime}, take_type=#{obj.takeType}, take_score=#{obj.takeScore}, def1=#{obj.def1}, def2=#{obj.def2}, def3=#{obj.def3}, def4=#{obj.def4},
where id=#{obj.id} AND platform_id = #{platformId}
