mapper.select:
		select role_id,menu_id from role_menu
		where 1=1
            AND role_id = #{obj.search.roleId}
            AND menu_id = #{obj.search.menuId}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select role_id,menu_id from role_menu
		where 1=1
            AND role_id = #{obj.roleId}
            AND menu_id = #{obj.menuId}

         AND platform_id = #{platformId}

mapper.update:
    	update role_menu 
        
           role_id=#{obj.roleId},
           menu_id=#{obj.menuId},  
        
		where role_id=#{obj.roleId}  and menu_id=#{obj.menuId} 
         AND platform_id = #{platformId}
