mapper.select:
		select ucia.* from user_completion_item_apply ucia
        left join activity_info a
        on ucia.activity_id=a.id
		where 1=1
            AND ucia.id = #{obj.id}
            AND  ucia.user_id = #{obj.userId}
            AND  ucia.user_name = #{obj.userName}
            AND  ucia.activity_id = #{obj.activityId}
            AND  ucia.competition_item_id = #{obj.competitionItemId}
            AND  ucia.competition_type = #{obj.competitionType}
            AND  ucia.create_time = #{obj.createTime}
            AND  ucia.apply_status = #{obj.applyStatus}
            AND  ucia.production_name = #{obj.productionName}
            AND  ucia.apply_group = #{obj.applyGroup}
            AND  ucia.apply_year_group = #{obj.applyYearGroup}
            AND  ucia.real_name = #{obj.realName}
            AND  ucia.birthday = #{obj.birthday}
            AND  ucia.sex = #{obj.sex}
            AND  ucia.school_name = #{obj.schoolName}
            AND  ucia.class_company = #{obj.classCompany}
            AND  ucia.recommened_company = #{obj.recommenedCompany}
            AND  ucia.card_type = #{obj.cardType}
            AND  ucia.card_number = #{obj.cardNumber}
            AND  ucia.telephone = #{obj.telephone}
            AND  ucia.mobile_phone = #{obj.mobilePhone}
            AND  ucia.email = #{obj.email}
            AND  ucia.postcode = #{obj.postcode}
            AND  ucia.address = #{obj.address}
            AND  ucia.idea_desc = #{obj.ideaDesc}
            AND  ucia.file_name = #{obj.fileName}
            AND  ucia.file_path = #{obj.filePath}
            AND  ucia.scriptwriter = #{obj.scriptwriter}
            AND  ucia.director = #{obj.director}
            AND  ucia.camerist = #{obj.camerist}
            AND  ucia.editor = #{obj.editor}
            AND  ucia.musician = #{obj.musician}
            AND  ucia.artist = #{obj.artist}
            AND  ucia.main_stuff = #{obj.mainStuff}
            AND  ucia.team_desc = #{obj.teamDesc}
            AND  ucia.approve_status = #{obj.approveStatus}
            AND  ucia.approve_user_id = #{obj.approveUserId}
            AND  ucia.approve_user_name = #{obj.approveUserName}
            AND  ucia.approve_time = #{obj.approveTime}
