            AND  ucia.review_point = #{obj.reviewPoint}
            AND  ucia.vdef1 = #{obj.vdef1}
            AND  ucia.vdef2 = #{obj.vdef2}
            AND  ucia.vdef3 = #{obj.vdef3}
            AND  ucia.vdef4 = #{obj.vdef4}
            AND  ucia.vdef5 = #{obj.vdef5}
            AND  ucia.vdef6 = #{obj.vdef6}
            AND  ucia.vdef7 = #{obj.vdef7}


         AND a.year = #{obj.year}
        order by ${sort}

select max(vdef2) 
from user_completion_item_apply
where activity_id=#{activityId}
AND competition_item_id=#{itemId}
AND competition_type=#{itemType}
AND apply_group=#{applyGroup}

select * from user_completion_item_apply 
order by CAST(vdef4 as SIGNED) desc
limit 0,${size}
