mapper.select:
		select id,type,user_id,operate_time,description,resource_id,resource_type,vdef1,vdef2,vdef3,vdef4,vdef5 from user_login_operate_log
		where 1=1
            AND id = #{obj.search.id}
            AND type = #{obj.search.type}
            AND user_id = #{obj.search.userId}
            AND operate_time = #{obj.search.operateTime}
            AND description = #{obj.search.description}
            AND resource_id = #{obj.search.resourceId}
            AND resource_type = #{obj.search.resourceType}
            AND vdef1 = #{obj.search.vdef1}
            AND vdef2 = #{obj.search.vdef2}
            AND vdef3 = #{obj.search.vdef3}
            AND vdef4 = #{obj.search.vdef4}
            AND vdef5 = #{obj.search.vdef5}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,type,user_id,operate_time,description,resource_id,resource_type,vdef1,vdef2,vdef3,vdef4,vdef5 from user_login_operate_log
		where 1=1
            AND id = #{obj.id}
            AND type = #{obj.type}
            AND user_id = #{obj.userId}
            AND operate_time = #{obj.operateTime}
            AND description = #{obj.description}
            AND resource_id = #{obj.resourceId}
            AND resource_type = #{obj.resourceType}
            AND vdef1 = #{obj.vdef1}
            AND vdef2 = #{obj.vdef2}
            AND vdef3 = #{obj.vdef3}
            AND vdef4 = #{obj.vdef4}
            AND vdef5 = #{obj.vdef5}

         AND platform_id = #{platformId}

mapper.update:
update user_login_operate_log
id=#{obj.id}, type=#{obj.type}, user_id=#{obj.userId}, operate_time=#{obj.operateTime}, description=#{obj.description}, resource_id=#{obj.resourceId}, resource_type=#{obj.resourceType}, vdef1=#{obj.vdef1}, vdef2=#{obj.vdef2}, vdef3=#{obj.vdef3}, vdef4=#{obj.vdef4}, vdef5=#{obj.vdef5},
where id=#{obj.id} AND platform_id = #{platformId}

mapper.select:
		select log.*,user.user_name as userName from user_login_operate_log log join user_info user on log.user_id=user.id
		where 1=1
            AND log.id = #{obj.search.id}
            AND log.type = #{obj.search.type}
            AND log.user_id = #{obj.search.userId}
            AND log.operate_time = #{obj.search.operateTime}
            AND log.description = #{obj.search.description}
            AND log.resource_id = #{obj.search.resourceId}
            AND log.resource_type = #{obj.search.resourceType}
            AND log.vdef1 = #{obj.search.vdef1}
            AND log.vdef2 = #{obj.search.vdef2}
            AND log.vdef3 = #{obj.search.vdef3}
            AND log.vdef4 = #{obj.search.vdef4}
            AND log.vdef5 = #{obj.search.vdef5}
           
            AND user.user_name = #{obj.search.name}

         order by ${obj.orderField} ${obj.orderType}
